mapper.select:
select count(*)
from user_device
where device_num = #{deviceNum} and user_phone = #{userPhone}

select u.user_phone,u.user_name,d.device_num,d.device_name,ud.expiry_date,ud.create_time from user_device ud,users u,deviceOut d where d.owner_phone=#{ownerPhone} and d.device_num=ud.device_num and ud.user_phone=u.user_phone
and d.device_num = #{deviceNum}
AND ud.user_phone = #{userPhone}
AND d.device_name LIKE concat(concat('%',#{deviceName}),'%')
limit #{currentPage},10

select count(*) from user_device ud,users u,deviceOut d where d.owner_phone=#{ownerPhone} and d.device_num=ud.device_num and ud.user_phone=u.user_phone
and d.device_num = #{deviceNum}
AND ud.user_phone = #{userPhone}
AND d.device_name LIKE concat(concat('%',#{deviceName}),'%')
limit #{currentPage},10

mapper.update:
update user_device
set expiry_date = #{expiryDate},update_time = #{updateTime}
where device_num = #{deviceNum} and user_phone = #{userPhone}
